import React, {useState, useEffect} from 'react'
import CommonSection from '../shared/CommonSection'
import '../styles/livingroom.css';
import SearchBar from './../shared/SearchBar'
import NewarrivalCard from './../shared/NewarrivalCard'
import newarrivalData from '../assets/data/newarrivals'
import Newsletter from './../shared/Newsletter'
import { Container } from 'reactstrap';
import {Col,Row} from 'reactstrap'



const LivingRooms = () => {


  const [pageCount, setPageCount] = useState(0)
  const [page, setPage] = useState(0)


  useEffect(()=>{
    const pages=Math.ceil(5/4)
    setPageCount(pages)
  },[page])
  return (
   <>
   <CommonSection title={"Living Room Furnitures"}/>
   <section>
    <Container>
      <Row>
        <SearchBar/>
      </Row>
    </Container>
   </section>
   

   <section classname ="pt-0">
    <Container>  
      <Row>
       {     
        newarrivalData?.map(newarrival=>(
        <Col lg='3' className="mb-4" key={newarrival.id}>
          <NewarrivalCard newarrival ={newarrival}/>
        </Col>
        ))
       } 
     
      </Row>
    </Container>
   </section>
   



   </>
  )
}

export default LivingRooms
